  public static void handle(int[] pageRatio, int[] fun, String namePrefix) throws IOException {
    BatchData batchData = new BatchData(TSDataType.INT32, true);
    long cnt = 1;
    for(int x:pageRatio){
      batchData.putTime(cnt);
      batchData.putInt(x);
      cnt+=x;
    }

    long maxTime = cnt;
    for(int i = 0; i<batchData.length();i++){
      batchData.setTime(i, maxTime-batchData.getTimeByIndex(i));
    }

    Lab2 lab2 = new Lab2();
    lab2.writeDataToCSV("0501DataForLab1/", batchData, "第"+namePrefix+"次压缩后压缩倍数图.csv",false);
  }

  public static void handle(List<Integer> pageRatioList, List<Integer> funList, String namePrefix, int ttlNum) throws IOException {
    int[] pageRatio =  new int[pageRatioList.size()+ttlNum];
    int[] fun = new int[funList.size()];
    for(int i = 0;i<pageRatioList.size();i++){
      pageRatio[i] = pageRatioList.get(i);
    }

    for(int i = 0;i<ttlNum;i++){
      pageRatio[i+pageRatioList.size()] = 1;
    }
    for(int i = 0;i<funList.size();i++){
      fun[i] = funList.get(i);
    }
    handle(pageRatio, fun, namePrefix);
  }

  public static void reserveArray(int[] nums){
    int low = 0;
    int high = nums.length;

    while(low<high){
      int tmp = nums[low];
      nums[low] = nums[high];
      nums[high] = tmp;
      low++;
      high--;
    }
  }

